//请求后台的方法
import request from "@/request";
export function getSystemInfosByPage(params) {
  return request({
    url: "/systemInfo/getSystemInfosByPage",
    method: "get",
    params: params
  });
}

export function addSystemInfo(params) {
  return request({
    url: "/systemInfo/addSystemInfo",
    method: "post",
    params
  });
}

export function delSystemInfo(params) {
  return request({
    url: "/systemInfo/delSystemInfo",
    method: "delete",
    params: params
  });
}

export function updateSystemInfo(params) {
  return request({
    url: "/systemInfo/updateSystemInfo",
    method: "post",
    params: params
  });
}
