import Vue from "vue";
import Router from "vue-router";
import store from "../store";
Vue.use(Router);
var router;
export default (router = new Router({
  routes: [
    {
      path: "/main",
      component: () => import("@/views/layout"),
      meta: {
        requireAuth: true, // 只要此字段为true，必须做鉴权处理
        title: "首页",
        icon: "cloud"
      },
      children: [
        // {
          // path: "",
         // name: "main",
         //  component: () => import("@/views/datashow"),
          // meta: {}
         //},
         {
          path:"",
          redirect: "/main/datashow"
         },
        {
          path: "datashow",
          name: "datashow",
          component: () => import("@/views/datashow"),
          meta: {    //元数据
            title: "数据展示",
            icon: "cloud"
          }
        },
		
		// {
		//   path: "eccShowRed",
		//   name: "eccShowRed",
		//   component: () => import("@/views/eccShowRed"),
		//   meta: {
		//     title: "模板",
		//     icon: "cloud"
		//   }
		// },

        {
          path: "user",
          name: "user",
          component: () => import("@/views/user"),
          meta: {
            title: "用户管理",
            icon: "user"
          }
        },
        {
          path: "role",
          name: "role",
          component: () => import("@/views/role"),
          meta: {
            title: "角色管理",
            icon: "user"
          }
        },
		
		{
			path: "menu",
			name: 'menu',
			component: () => import('@/views/menu'),
			meta: {
				title: '菜单管理',
				icon: 'user'
			}
		},
		
		{
			path: "timeTask",
			name: 'timeTask',
			component: () => import('@/views/timeTask'),
			meta: {
				title: '定时任务管理',
				icon: 'cloud'
			}
		},
    {
      path: "systemInfo",
      name: 'systemInfo',
      component: () => import('@/views/systemInfo'),
      meta: {
        title: '农商行信息维护',
        icon: 'cloud'
      }
    },
      ]
    },
    {
      path: "/404",
      name: "404",
      component: () => import("@/views/notFind")
    },
    {
      path: "",
      redirect: "/main"
    },
    {
      path: "*",
      redirect: "/404"
    },
    {
      path: "/login",
      component: () => import("@/views/login")
    }
  ]
}));

 router.beforeEach((to, from, next) => {
   if (to.matched.some(res => res.meta.requireAuth)) {
     if (store.state.token == "" || store.state.token == undefined) {
       next({
         path: "/login",
         query: {
           redirect: to.fullPath
        }
       });
     } else {
       next();
     }
   } else {
     next();
   }
 });
