//请求后台的方法
import request from "@/request";

export function getCityInfosByPage(data) {
  return request({
    url: "/cityInfo/getCityInfoByPage",
    method: "post",
    data
  });
}

export function addCityInfo(data) {
  return request({
    url: "/cityInfo/addCityInfo",
    method: "post",
    data
  });
}

export function delCityInfo(params) {
  return request({
    url: "/cityInfo/delCityInfo",
    method: "delete",
    params: params
  });
}

export function updateCityInfo(data) {
  return request({
    url: "/cityInfo/updateCityInfo",
    method: "post",
	data
  });
}
